import java.util.TimerTask;

public class MainActivity extends AppCompatActivity {
    int LogoMargin = 15;
    int SearchBarMargin = 15;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        setContentView(R.layout.activity_main);
        ImageView navBar = findViewById(R.id.navBar);
        DrawerLayout drawerLayout = findViewById(R.id.drawerLayout);
        NavigationView navigationView = findViewById(R.id.NavigationView);
        RecyclerView FDRecycle = findViewById(R.id.FDRecycle);

        ///Animation on Scroll

        ImageView Logo = findViewById(R.id.Logo);
        ConstraintLayout SearchBar = findViewById(R.id.SearchBar);



        final Handler handler = new Handler(); // Create a new Handler

        Timer timer2 = new Timer(); // Create a new Timer
        TimerTask testing = new TimerTask() {
            public void run() {
                handler.post(new Runnable() {
                    public void run() {
                        // Update Logo's position
                        final ConstraintLayout.LayoutParams logoLayoutParams = (ConstraintLayout.LayoutParams) SearchBar.getLayoutParams();
                        logoLayoutParams.setMargins(0, 0, SearchBarMargin, 0);
                        SearchBar.setLayoutParams(logoLayoutParams);
                        LogoMargin = LogoMargin - 5;

                       //  Update SearchBar's position
//                        final ConstraintLayout.LayoutParams logoLayoutParams1 = (ConstraintLayout.LayoutParams) SearchBar.getLayoutParams();
//                        logoLayoutParams1.setMargins(SearchBarMargin, 0, 0, 0);
//                        SearchBar.setLayoutParams(logoLayoutParams1);

                        SearchBarMargin = SearchBarMargin + 10;

                        if (LogoMargin < -160) {
                            timer2.cancel(); // Cancel the timer when LogoMargin is less than -160
                        }
                    }
                });
            }
        };

        timer2.schedule(testing, 0, 1000); // Schedule the TimerTask to run repeatedly with a fixed delay of 2000 milliseconds (2 seconds)


        // RecycleView SetUp
        ArrayList<FDModel> fdModels = new ArrayList<>();
        fdModels.add(new FDModel("", ""));
        fdModels.add(new FDModel("Bangalore", "https://img.staticmb.com/mbcontent/images/crop/uploads/2023/4/Bangalore-inner-road_0_1200.jpg"));
        fdModels.add(new FDModel("Chennai", "https://www.incredibleindia.org/content/dam/incredible-india-v2/images/places/chennai/Original.jpg/jcr:content/renditions/cq5dam.web.1800.737.jpeg"));
        fdModels.add(new FDModel("Delhi", "https://cdn.britannica.com/37/189837-050-F0AF383E/New-Delhi-India-War-Memorial-arch-Sir.jpg"));
        fdModels.add(new FDModel("Gurgaon", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT3K9V46ba--6ZiW---Bkac0nQ56oqlIjqlDQ&usqp=CAU"));
        fdModels.add(new FDModel("Hyderabad", "https://a.travel-assets.com/findyours-php/viewfinder/images/res70/68000/68045-Hyderabad-And-Vicinity.jpg"));
        fdModels.add(new FDModel("Mumbai", "data:image/jpeg;base64,"));
        fdModels.add(new FDModel("Noida", "data:image/jpeg;base64,"));
        fdModels.add(new FDModel("Pune", "data:image/jpeg;base64,"));
        fdModels.add(new FDModel("All Cities", "https://img.freepik.com/premium-vector/location-icon-isolated-white-background-vector-illustration_736051-695.jpg"));

        FDAdapter fdAdapter = new FDAdapter(MainActivity.this, fdModels);
        FDRecycle.setLayoutManager(new LinearLayoutManager(MainActivity.this, LinearLayoutManager.HORIZONTAL, false));
        FDRecycle.setAdapter(fdAdapter);


        View HeaderView = navigationView.getHeaderView(0);

        ConstraintLayout constraintLayout = HeaderView.findViewById(R.id.drawer);
        constraintLayout.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Toast.makeText(MainActivity.this, "drawer", Toast.LENGTH_SHORT).show();
                drawerLayout.close();
            }
        });

        navigationView.setNavigationItemSelectedListener(new NavigationView.OnNavigationItemSelectedListener() {
            @Override
            public boolean onNavigationItemSelected(@NonNull MenuItem item) {
//item.setContentDescription("this is a button");
                if (item.getItemId() == R.id.OYOWizardBlue) {

                    Toast.makeText(MainActivity.this, "Oyo_Wizard_Bl425ue", Toast.LENGTH_SHORT).show();
                }
                if (item.getItemId() == R.id.OYOForBusiness) {
                    Toast.makeText(MainActivity.this, "Oyo_Wizard_Blue345", Toast.LENGTH_SHORT).show();
                }
                return false;
            }
        });

        navBar.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                drawerLayout.open();
            }
        });
        Toast.makeText(this, "test test .. 365365365356356365365365365365365356356365356365", Toast.LENGTH_SHORT).show();
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.drawer_item, menu);

        // Access the menu item
        MenuItem menuItem = menu.findItem(R.id.OYOWizardBlue);

        // Access the custom layout within the menu item
        View actionView = menuItem.getActionView();
        TextView titleTextView = actionView.findViewById(R.id.first_line);
        TextView contentDescriptionTextView = actionView.findViewById(R.id.second_line);
        ImageView imageView = findViewById(R.id.image);
        imageView.setImageResource(R.drawable.wizard);

        // Customize the TextViews as needed
        titleTextView.setText("OYO Wizard - Blue");
        contentDescriptionTextView.setText("Valid till 08Apr 2024");

        return true;
    }


}